import React, { useState } from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";
const NewExpense = (props) => {
  const [hidden, show] = useState(false);
  const formShow = () => {
    show(true);
    return <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />;
  };
  const formHidden = () => {
    hidden(false);
  };
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  };
  return (
    <div className="new-expense">
      {!formShow && (
        <button name="addButton" type="submit" onClick={formShow}>
          Add New Expense
        </button>
      )}
      {formShow && <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />}
    </div>
  );
};
export default NewExpense;
